const Modal = ({ show, onClose, birdDetails }) => {
  if (!show) return null;

  return (
    <div className="modal">
      <div className="modal-content">
        <h2>Adopt {birdDetails.name}</h2>
        <p><span className="highlight">Size:</span> {birdDetails.size}</p>
        <p><span className="highlight">Lifespan:</span> {birdDetails.lifespan}</p>
        <p><span className="highlight">Personality:</span> {birdDetails.personality}</p>
        <p><span className="highlight">Care:</span> {birdDetails.care}</p>
        <p><span className="highlight">Features:</span> {birdDetails.features}</p>
        <a href={birdDetails.learnMoreLink} target="_blank" rel="noopener noreferrer">Learn more about {birdDetails.name}</a>
        <button onClick={onClose} className="close-button">Close</button>
      </div>
    </div>
  );
};

