import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Cows.css";

const CowDetailsModal = ({ cow, onClose }) => {
  if (!cow) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>{cow.name}</h2>
        <p>
          <span className="highlight">Breed</span>: {cow.breed}
          <br />
          <span className="highlight">Lifespan</span>: {cow.lifespan}
          <br />
          <span className="highlight">Size</span>: {cow.size}
          <br />
          <span className="highlight">Features</span>: {cow.features}
          <br />
          <span className="highlight">Personality</span>: {cow.personality}
          <br />
          <span className="highlight">Care</span>: {cow.care}
          <br />
          <span className="highlight">Current Price</span>: {cow.price}
          <br />
          <a href={cow.link} target="_blank" rel="noopener noreferrer">
            More Information
          </a>
        </p>
        <button className="modal-close-button" onClick={onClose}>
          Close
        </button>
      </div>
    </div>
  );
};

