import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Goats.css";

const GoatDetailsModal = ({ goat, onClose }) => {
  if (!goat) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>{goat.name}</h2>
        <p>
          <span className="highlight">Size</span>: {goat.size}
          <br />
          <span className="highlight">Lifespan</span>: {goat.lifespan}
          <br />
          <span className="highlight">Personality</span>: {goat.personality}
          <br />
          <span className="highlight">Care</span>: {goat.care}
          <br />
          <span className="highlight">Features</span>: {goat.features}
          <br />
          <span className="highlight">Current Price</span>: {goat.price}
          <br />
          <a href={goat.link} target="_blank" rel="noopener noreferrer">
            More Information
          </a>
        </p>
        <button className="modal-close-button" onClick={onClose}>
          Close
        </button>
      </div>
    </div>
  );
};

